mapper.update:
UPDATE t_category
SET category_nm = #{categoryNm}
WHERE category_pk = #{categoryPk}

UPDATE t_category A
LEFT JOIN t_purchase_product B
ON A.category_pk = B.category_pk
SET B.category_pk = #{categoryPk}
WHERE B.product_pk = #{productPk}

mapper.insert:
INSERT INTO t_user
SET uid = #{uid}
, upw = #{upw}
, nm = #{nm}

mapper.select:
SELECT user_pk AS userPk, uid, upw, nm, created_at AS createdAt
FROM t_user
WHERE uid = #{uid}
